const e=e=>e[Math.floor(Math.random()*e.length)],t=()=>e("あいうえおかきくけこさしすせそたちつてとなにぬねのはひふへほまみむめもやゆよらりるれろわんがぎぐげござじずぜぞだじづでどばびぶべぼぱぴぷぺぽ"),r=()=>{const r=[t(),t()],n=`${e("きしちにひみりぎじぢびぴ")}${e("ゃゅょ")}`;if(1!==Math.floor(5*Math.random()))return`${r[0]}${r[1]}`.repeat(2);switch(Math.floor(6*Math.random())){case 0:return`${n}${r[0]}`.repeat(2);case 1:return`${r[0]}${n}`.repeat(2);case 2:return`${n}っ${n}`;case 3:return`${r[0]}っ${r[1]}${r[0]}${r[1]}`;case 4:return`${r[0]}っ${n}${r[0]}${n}`;case 5:return`${r[0]}${n}っ`.repeat(2);default:return""}},n=document.querySelector("#content"),o=document.querySelector("#regenerate"),a=document.querySelector("#tweet"),c=document.querySelector("#github"),i=()=>n.innerHTML=r();document.addEventListener("DOMContentLoaded",i),o.addEventListener("click",i),a.addEventListener("click",(()=>window.open(`https://twitter.com/intent/tweet?hashtags=擬音ジェネレーター&url=https://hijiki02.github.io/GION/&text=${n.innerHTML}`))),c.addEventListener("click",(()=>window.open("https://github.com/hijiki02/GION")));
